public class Ejer_4_MultiplosDe5conWHILE {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 0;
		
		while(num<=100) {
			
			System.out.println(num);
			num = num+5; //para optimizar esto sería "num+=5"
			
			}
		
	}

}
